import React, { useState, useEffect } from 'react';

export function ServicesPage() {

  const [szolg, setSzolg] = useState([]);
  const [isFetchPending, setFetchPending] = useState(false);

  useEffect(() => {
    setFetchPending(true);
    fetch("https://localhost:6969/Szolgaltatas")
      .then((res) => res.json())
      .then((Szolg) => setSzolg(Szolg))
      .catch(console.log)
      .finally(() => {
        setFetchPending(false);
      });
  }, []);

  return (
    <div>
      {/* SZOLGÁLTATÁSAINK */}
      <h1 className="cim">Szolgáltatásaink</h1>
      <div style={{ padding: '20px' }}>
        <div className="container">
         
            {szolg.map((sz) =>{
              if (sz.id % 2 == 0) {
                console.log("a");
                return ( 
                <div className="row">
                  <div key={sz.id}>
                    <div className="col-sm-5">
                      <div><img src={`data:image/webp;base64, ${sz.kepfajl}`} className="img-fluid" alt='kep' /></div>
                    </div>
                    <div className="col-sm-7" style={{ margin: 'auto' }}>
                      <h1 className="sotet">{sz.nev}</h1>
                      <p className="sotet">{sz.leiras}</p>
                    </div>
                  </div></div>
                )
              }
              else {
                console.log("b");
                return (
                  <div className="row p-4">
                  <div key={sz.id}>
                    <div className="col-sm-7" style={{ margin: 'auto' }}>
                      <h1 className="sotet">{sz.nev}</h1>
                      <p className="sotet">{sz.leiras}</p>
                    </div>
                    <div className="col-sm-5">
                      <div><img src={`data:image/webp;base64, ${sz.kepfajl}`} className="img-fluid" /></div>
                    </div>
                  </div></div>
                )
              } 
            })}
          </div>
        
      </div>
      {/* SZOLGÁLTATÁSAINK vége */}
    </div>
  )
  
}

export default ServicesPage;
